import UIKit

protocol SharePortfolioBusinessLogic
{
    func fetchPortfolio() 
}

protocol SharePortfolioDataStore
{
    //var name: String { get set }
}

class SharePortfolioInteractor: SharePortfolioBusinessLogic, SharePortfolioDataStore
{
    var presenter: SharePortfolioPresentationLogic?
    var worker: SharePortfolioWorker?
    //var name: String = ""
    
    // MARK: Do something
    
    func fetchPortfolio() {
        let portfolio = PortfolioWorker.sharedInstance.portfolio!
        let resp = SharePortfolio.FetchPortfolio.Response(name: portfolio.name, value: portfolio.marketValue, assets: portfolio.assets, initialValue: portfolio.initialValue, change24H: portfolio.marketValueChange)
        self.presenter?.presentPortfolio(response: resp)
        
    }
}
